  locale: "ar",
};

function App() {
  return (
    <Layout>
      <Helmet encodeSpecialCharacters={true}>
        <script
          src="https://code.jquery.com/jquery.js"
          type="text/javascript"
        ></script>

        <script type="text/javascript">{`${console.log(wpwlOptions)}`}</script>
        <script
          async
          type="text/javascript"
          src="https://eu-test.oppwa.com/v1/paymentWidgets.js?checkoutId=B0167C83BCCE9F534D4775EE6399EF82.uat01-vm-tx01"
        ></script>
      </Helmet>

      <form
        action="localhost:300"
        className="paymentWidgets"
        data-brands="VISA MASTER AMEX"
      ></form>

      {/* <Task /> */}
    </Layout>
  );
}

export default App;
