import React, { FC, useState } from "react";
import { Button, Input } from "semantic-ui-react";
import { IListTaskDto } from "../../types/types";
import "./task.scss";

const Task: FC = () => {
  const [list, setList] = useState<IListTaskDto[]>([{ title: "", status: 0 }]);
  const [taskName, setTaskName] = useState<string>("");

  const onAddTaskClick = () => {
    const temp: IListTaskDto[] = [...list];
    temp.push({
      status: 0,
      title: taskName,
    });

    setList(temp);
  };

  return (
    <div className="Task">
      <h5 className="name-user">Hello Hashem!</h5>

      <section className="form-countiner">
        <h2>You have 3 tasks today</h2>
        <section className="row-view">
          <Input placeholder="What you have today ?" className="input-style" />
          <Button
            onClick={onAddTaskClick}
            primary
            className="btn-style"
            content="Add Task"
          />
        </section>
      </section>

      <main className="list-coutniner"></main>
    </div>
  );
};

export default Task;
