import React, { FC, useState } from "react";
import { Button, Input } from "semantic-ui-react";
import ListTaskItem from "../../components/listTaskItem/ListTaskItem";
import { IListTaskDto } from "../../types/types";
import "./task.scss";

const Task: FC = () => {
  const [taskName, setTaskName] = useState<string>("");

  const onAddTaskClick = (): void => {
    const temp: IListTaskDto[] = [...list];
    temp.push({
      status: 0,
      title: taskName,
    });

    setList(temp);
  };

  const onProgressClick = (index: number): void => {
    const temp: IListTaskDto[] = [...list];
    temp[index] = { ...temp[index], status: 1 };

    setList(temp);
  };

  return (
    <div className="Task">
      <section className="form-countiner">
        <h5 className="name-user">Hello Hashem!</h5>
        <h2>You have {list.length} tasks today</h2>
        <section className="row-view">
          <Input
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
            placeholder="What you have today ?"
            className="input-style"
          />
          <Button
            primary
            className="btn-style"
            content="Add Task"
            onClick={onAddTaskClick}
          />
        </section>
      </section>

      <main className="list-coutniner">
        <ListTaskItem onProgressClick={onProgressClick} list={list} />
      </main>
    </div>
  );
};

export default Task;
